var SimpleNotifications={constants:{notificationOnScreenTime:3e3},create:function(a,b){var c=SimpleNotifications.generateId(),d="<div id='"+c+"' class='simple-notification "+b+"'>"+a+"</div>";document.body.innerHTML+=d,setTimeout(function(){SimpleNotifications.remove(c)},SimpleNotifications.constants.notificationOnScreenTime)},generateId:function(){return"e"+(new Date).getTime().toString()+Math.floor(100*Math.random()).toString()},remove:function(a){var b=document.querySelector("#"+a);b.parentNode.removeChild(b)}};